import React from 'react'
import {useHistory} from "react-router-dom"
import { useForm } from "react-hook-form";

const Addproduct = ({onAdd}) => {
    let history = useHistory();
        const [valueInput, setValueInput] = React.useState({});

    const onHandleChange = (e) => {
        const {name,value} = e.target;
        setValueInput(
            {...valueInput,[name]: e.target.value}
        );
    }
    const onHandleSubmit = (e) => {
        //e.preventDefault();
            onAdd(valueInput);
        history.push("/");
    };
    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = valueInput => console.log(valueInput);
    return (
        <div>
          
          <h3>Add New Product</h3>
                <form onSubmit= {handleSubmit (onHandleSubmit) } >
                    <div className="form-group" >
                        <label> Name:  </label>
                        <input type="text" className="form-control" 
                        placeholder="Name" onChange={onHandleChange} name="name" 
                        ref={register({required:true, minleng : 5})}/>
                        {errors.name && errors.type=="required"&&<span>không được để trống</span>}
                        {errors.name && errors.type=="required"&&<span>nhập ít nhất 5 ký tự</span>}
                    </div>
                    <div className="form-group">
                        <label>Imge </label>
                        <input type="text" className="form-control" placeholder="Image" onChange={onHandleChange} name="Image" />
                    </div>
                    <div className="form-group">
                        <label>Price </label>
                        <input type="text" className="form-control" placeholder="Price" onChange={onHandleChange}  name="Price" />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Add Product" className="btn btn-primary"/>
                    </div>
                </form>
            
        </div>
    )
}

Addproduct.propTypes = {

}

export default Addproduct
