import React, { useState } from 'react';
import ProductsManager from './pages/views/Admin/Products';
import Datafake from './DataFake';
import Footer from './components/Main/Footer';
import Header from './components/Main/Header';
import Navbar from './components/Main/NavBar';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
//import AddProduct from './components/AddProduct';
function App() {
  const [products, setProduct] = React.useState(Datafake);
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProduct(newProducts);
  }
  const onHandleAdd = (product) => { //them moi 1 san pham 
    const newProduct = {
      id: products.length + 1, ...product
    }

    setProduct([...products, newProduct])

  }
  return (

      <Router>
    <div>
        <Navbar></Navbar>
      <div id="layoutSidenav">
        <Sidebar></Sidebar>
        <div id="layoutSidenav_content">
          <main>
            <div className="container-fluid">
                <Switch>
                    <Route path="/" exact><Product products={products} onRemove={onHandleRemove}></Product></Route>
                    <Route path="/addproduct"><Addproduct onAdd={onHandleAdd} /></Route>

              </Switch>
            </div>
          </main>
         <Footer></Footer>
        </div>
      </div>
    </div>
    </Router>

  )
  }

export default App;
